import styled from 'styled-components';

export const SearchContainer = styled.div`
    background-color: #EEF2F4;
    width: 300px;
    height: 30px;
    border-radius: 15px;
    padding: 0 20px;
    display: flex;
    flex-direction: row;

    input {
        color: #707B81;
        outline: 0;
        width: 250px;
    }
`;
