import styled from 'styled-components';

export const ItemContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    border: 0.3px solid #CCCFD1;
    height: 33px;
    top: inherit;

    #sub-item-titulo {
        display: flex;
        flex-direction: row;
        align-items: center;
    }

    .sub-container {
        display: flex;
        flex-direction: row;
    }

    .sub-item {
        background-color: #dedede;
        padding: 0 10px;
        display: flex;
        align-items: center;
        flex-direction: column;
    }
`;

export const Mark = styled.div`
    width: 5px;
    height: 32px;
    background-color: #4C309B;
    border-radius: 15px;
`;

export const ItemNomeProduto = styled.p`
    font-size: 12px;
    color: #4C309B;
    padding-left: 10px;
    line-height: 0;
`;

export const ItemTitulo = styled.p`
    font-size: 8px;
    color: #4C309B;
    margin-bottom: 0;
`;

export const ItemValor = styled.p`
    font-size: 14px;
    font-weight: bold;
    color: #4C309B;
    margin-top: 0;
`;
