import React, { useEffect, useState } from 'react';
import { ContainerHeader, ContainerList } from './AppStyles';
import SearchBar from './components/SearchBar';
import Item from './components/Item';
import axios from 'axios';

const App = () => {

  const [lista, setLista] = useState([]);
  const [inputTxt, setInputTxt] = useState('');

  useEffect(() => {
    axios.get('https://ed87c2a9-bcc4-4e0c-8fd2-fefb9875b65b.mock.pstmn.io/getStockConsolidation')
      .then(res => {
        console.log(res);
        setLista(res.data.data.products);
    });
  }, []);

  async function handleChange(query) {
    await setInputTxt(query);
    let listaAux = this.props.list.filter( function (item) {
      return item.productName;
    });
  }

  return (
    <div style={{ paddingLeft: 200, paddingRight: 200, paddingTop: 20, paddingBottom: 20, backgroundColor: '#F8FAFB' }}>
      <ContainerList>
        <ContainerHeader>
          <p>Fundos</p>
          <SearchBar
            value={inputTxt}
            onchange={e => handleChange(e.target.value)}
          />
        </ContainerHeader>

        {lista.map(produto => 
          <Item
              titulo={produto.productName}
              saldoAtual="00,00"
              quant="3"
              precoMedio="00,00"
              ultimaCotacao="00,00"
              yield1M="0%"
              yield12M="0%"
              carteira="0%"
            />
          )}
      </ContainerList>
    </div>
  );
}

export default App;
